import React, {useState,useEffect,useRef} from 'react';
import FlashCardList from './FlashCardList';
import './app.css'
import axios from 'axios'

function App() {
  const [flashcards, setFlashcards] = useState(SAMPLE_FLASHCARDS)
  const [categories, setCategories] = useState([])
  const url = "https://opentdb.com/api.php?amount=10"
  const urlCat = "https://opentdb.com/api_category.php";
  const categoryEl = useRef()
  useEffect(()=> {
    axios.get(urlCat)
    .then(res => {
     setCategories(res.data.trivia_categories)
    })
  },[])
  useEffect(() => {
    axios.get(url)
    .then(res=> {
      setFlashcards(res.data.results.map((questionItem,index)=> {
        const answer =  decodeString(questionItem.correct_answer)
        const options = [...questionItem.incorrect_answers.map(a=> decodeString(a))
          ,answer]
        return {
          id:`${index}-${Date.now()}`, // to overchek it to be unique
          question:decodeString(questionItem.question),
          answer:questionItem.correct_answer,
          options:options.sort(()=> Math.random() - .5)
        }
      }))
    })

  }, [])

  function decodeString(str) {
    const textArea = document.createElement('textarea')
    textArea.innerHTML = str
    return textArea.value
  }

  function handelSubmit(e){
    e.preventDefault();

  }
  return (
    <>
    <form className="header" onSubmit={handelSubmit}>
      <div className="form-group">
        <label htmlFor="category">Category</label>
        <select id="category" ref={categoryEl}>
          {categories.map(category => {
            return  <option value={categories.id} key={category.id}>{category.name}</option>
          })}
        </select>
      </div>
    </form>
    <div className="container">
        <FlashCardList flashcards={flashcards}/>
    </div>
    </>
  )
}
const SAMPLE_FLASHCARDS = [
  {
    id:1,
    question: 'What is 2+2 ?',
    answer: '4',
    options: [
      '2',
      '3',
      '4',
      '5'
    ]
  },
  {
    id:2,
    question: 'Questions 2 ?',
    answer: 'Answer',
    options: [
      'Answer 1',
      'Answer 2',
      'Answer 3',
      'Answer'

    ]
  }
]
export default App;
